import React, { useEffect, useState } from 'react'
import JobItem from './JobItem'
import JobSearch from '../JobSearch/JobSearch'

import './JobList.css'

const JobList = () => {
  const [jobs, setJobs] = useState([])
  onst[search, setSearch] = useState('')

  useEffect(() => {
    getJobs()
  }, [])

  const getJobs = async () => {
    const response = await fetch(
      `https://search.bossjob.com/api/v1/search/job_filter?size=10&query=system`
    )
    const data = await response.json()
    console.log('All data >>>>>', data)
    setJobs(data.data.jobs)
    console.log(data.data.jobs)
  }
  return (
    <div>
      <JobSearch
        value={search} />
      <div className="content">
        <div className="found-job">1321 jobs found</div>
        {
          jobs.map(job => (
            <JobItem
              key={job.id}
              title={job.job_title}
              salary_range_from={job.salary_range_from}
              salary_range_to={job.salary_range_to}
              job_country={job.job_country}
              xp_lvl={job.xp_lvl}
              degree={job.degree}
              job_type={job.job_type}
              company_logo={job.company_logo}
              company_name={job.company_name}
              updated_at={job.updated_at} />
          ))
        }
      </div>
    </div>
  )
}

export default JobList
